import React, { Component } from "react";
import { connect } from "react-redux";
import { ListGroup, ListGroupItem } from "reactstrap";
import { bindActionCreators } from "redux";
import {
  changeCategory,
  getCategories,
} from "../../redux/actions/categoryActions";

class CategoryList extends Component {
  componentDidMount = () => {
    debugger;
    this.props.actions.getCategories();
    console.log(this.props.categories.length);
  };

  render() {
    debugger;

    return (
      <>
        <div> CategoryList- {this.props.categories.length} </div>
        <ListGroup>
          {this.props.categories.map((item, index) => (
            <ListGroupItem
            active ={ this.props.currentCategory.categoryName === item.currentCategory.categoryName  }
              onClick={() => this.props.actions.changeCategory(item)}
              key={index}
            >
              {item.categoryName}
            </ListGroupItem>
          ))}
        </ListGroup>
        <p>Current Category : {this.props.currentCategory.categoryName}</p>
      </>
    );
  }
}

function mapStateToProps(state) {
  debugger;
  return {
    currentCategory: state.changeCategoryReducer,
    categories: state.categoryListReducer,
  };
}
function mapDispatchToProps(dispatch) {
  return {
    actions: {
      getCategories: bindActionCreators(getCategories, dispatch),
      changeCategory: bindActionCreators(changeCategory, dispatch),
    },
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(CategoryList);
