import React, { Component } from "react";
import { connect } from "react-redux";
import { ListGroup, ListGroupItem } from "reactstrap";
import { bindActionCreators } from "redux";
import { getCategories } from "../../redux/actions/categoryActions";

class CategoryList extends Component {
  componentDidMount = () => {
  };

  render() {
      debugger;

    return (
      <>
        <div> CategoryList </div>
        <ListGroup>
          {this.props.dispatch.getCategories.map((item, index) => (
            <ListGroupItem key={index}>{item.categoryName}</ListGroupItem>
          ))}
        </ListGroup>
        <p>Current Category : {this.props.currentCategory.categoryName}</p>
      </>
    );
  }
}

function mapStateToProps(state) {
  debugger;
  return {
    currentCategory: state.changeCategoryReducer,
  };
}
function mapDispatchToProps(dispatch) {
  return {
    actions: {
      getCategories: bindActionCreators(getCategories),
    },
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(CategoryList);
