import React, { Component } from "react";
import { connect } from "react-redux";
import {
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem,
  NavItem,
  NavLink,
  Badge,
} from "reactstrap";
import { bindActionCreators } from "redux";
class CartSummary extends Component {
  renderEmpty = () => {
    return (
      <UncontrolledDropdown nav inNavbar>
        <DropdownToggle nav caret>
          {`My Cart ${this.props.cartList.length}`}
        </DropdownToggle>
        <DropdownMenu right>
          {this.props.cartList.map((item) => (
            <DropdownItem key={item.product.id}>
              {item.product.productName} -<Badge color="success" pill> {item.quantity}</Badge>
            </DropdownItem>
          ))}
          <DropdownItem divider />
          <DropdownItem>Sepete Git</DropdownItem>
        </DropdownMenu>
      </UncontrolledDropdown>
    );
  };
  render() {
    return (
      <div>
        {this.props.cartList.length > 0 ? (
          this.renderEmpty()
        ) : (
          <NavItem>
            <NavLink>Cart is empty.</NavLink>
          </NavItem>
        )}
      </div>
    );
  }
}

function mapStateToProps(state) {
  return {
    cartList: state.cartReducer,
  };
}
// function mapDispatchToProps(dispatch) {
//   return {
//     actions: {
//         getCartList : bindActionCreators(getCartList,dispatch)
//     },
//   };
// }
export default connect(mapStateToProps)(CartSummary);
